import React from "react";


  const { match: { path, isExact } } = this.props;
  const backgroundStyle = path === '/books/:id' && isExact === true ? { backgroundColor: 'blue' } : { backgroundColor: 'orange' };
  const navbarStyles = {
    color: 'white', 
    backgroundStyle
  };
class Nav extends Component {
render() {
  return (
    <div style={{ navbarStyles }}>
    <nav className="navbar">
    <a className="navbar-brand" href="/">Google Reads!</a>
    </nav>
    </div>
  )
};
}

export default Nav;
